import React, { useState, useEffect} from "react";
import Axios from "axios";

import CoverImg from "../../Assests/Images/background/bg-1.png";
import { FaUniversity } from "react-icons/fa";


function Profile(props) {

  const userID = props.userID;

  const [viewName, setViewName] = useState("");
  const [viewProfile, setViewProfile] = useState("avatar/male.svg");
  const [viewEducation, setViewEducation] = useState([]);

  useEffect(() => {
    Axios.get(`${process.env.REACT_APP_SERVER_DOMAIN}/home/userProfile/` + userID)
    .then((response) => {
      const profileDetail1 = response.data[0];
      const profileDetail2 = response.data[1];
      const profileDetail3 = response.data[2];
      const profileDetail4 = response.data[3];
      setViewName(profileDetail1[0].name);
      setViewEducation(profileDetail4[0].school);

      if (profileDetail2.length === 0) {
        if (profileDetail3[0].gender === 'Male') {
        setViewProfile("avatar/male.svg");
        } else {
          setViewProfile("avatar/female.svg");
        }
      } else {
        const profile = "user/"+profileDetail2[0].profile_photo;
        setViewProfile(profile);
      }
    });
  }, [userID]);

  return (
          <div className="card mb-3">
            <img
              src={CoverImg}
              className="card-img-top profilecover"
              alt="Background Cover"
            />
            <div className="position-relative">
              <div className="position-absolute top-50 start-50 translate-middle w-50">

              <img
                  src={require(`../../Assests/Images/${viewProfile}`).default}
                  className="card-img-top rounded-circle"
                  alt="Background Cover"
                /> 
              </div>
            </div>
            <div className="card-body mt-4 text-center pb-0 px-1">
              <h6 href="/edcwe" className="card-title pt-3 text-truncate">
                <a className="text-decoration-none link-dark" href="/profile">
                {viewName}
                </a>
              </h6>
              <p className="card-text text-muted text-small">
                <FaUniversity className="icon" /> {viewEducation}
              </p>
            </div>
            <hr className="dropdown-divider" />
            <div className="mx-auto m-1">
              <a href="/profile" className="text-decoration-none link-primary">
                View Profile
              </a>
            </div>
          </div>
        );
  }

export default Profile;
