import React, { useState, Fragment } from "react";
import { Container, Row, Col, Form } from "react-bootstrap";
import UserImg from "../../Assests/Images/user/sha.jpeg";

import { FcCamera, FcVideoCall } from "react-icons/fc";
import { BsXCircleFill } from "react-icons/bs";

import { toast } from "react-toastify";

function CreatePost() {
  //Text Input
  const [postText, setpostText] = useState("");

  //Image Input
  const [postImage, setPostImage] = useState();
  const [postVideo, setPostVideo] = useState();
  const [imageLength, setImageLength] = useState(false);
  const [videoLength, setVideoLength] = useState(false);

  //IMAGE INPUT
  const imageChange = (event) => {
    let imageFile = event.target.files[0];

    if (event.target.files && event.target.files.length > 0) {
      setPostImage(imageFile);
      setImageLength(true);
    }

    if (event.target.files[0].size > (5e+6)) {
      setPostImage();
      setImageLength(false);
      return toast.warn(
        <Fragment>
          <span>Please upload a file smaller than 5 MB</span>
        </Fragment>
      );
    }

    if (!imageFile.name.match(/\.(jpg|jpeg|png|gif)$/)) {
      setPostImage();
      setImageLength(false);
      return toast.warn(
        <Fragment>
          <span>
            Invalid File Upload. Please Try to Upload file with jpg, jpeg, png
            or gif
          </span>
        </Fragment>
      );
    }
  };

  const removeSelectedImage = () => {
    setPostImage();
    setImageLength(false);
  };

  //VIDEO INPUT
  const videoChange = (event) => {
    let videoFile = event.target.files[0];

    if (event.target.files && event.target.files.length > 0) {
      setPostVideo(videoFile);
      setVideoLength(true);
    }

    if (event.target.files[0].size > (5e+7)) {
      setPostVideo();
      setVideoLength(false);
      return toast.warn(
        <Fragment>
          <span>Please upload a file smaller than 50 MB</span>
        </Fragment>
      );
    }

    if (!videoFile.name.match(/\.(mp4)$/)) {
      setPostVideo();
      setVideoLength(false);
      return toast.warn(
        <Fragment>
          <span>Invalid File Upload. Please Try to Upload file with mp4</span>
        </Fragment>
      );
    }
  };
  const removeSelectedVideo = () => {
    setPostVideo();
    setVideoLength(false);
  };

  //Form Input
  let createPostFormIsValid = false;

  if (postText !== "" || imageLength || videoLength) {
    createPostFormIsValid = true;
  }

  const createPostFormSubmissionHandler = (event) => {
    event.preventDefault();
    alert("okay");
  };

  return (
    <div className="card py-3">
      <Container>
        <Row className="mx-auto my-2">
          <Col sm={1} className="d-flex">
            <img
              src={UserImg}
              className="img-fluid my-auto rounded-circle"
              alt="zom-in user"
            />
          </Col>
          <Col sm={11}>
            <div className="d-grid gap-2 createPost">
              <button
                className="btn btn-outline-secondary rounded-pill text-start p-3 fw-bold"
                type="button"
                data-bs-toggle="modal"
                data-bs-target="#staticBackdrop"
              >
                Share your thoughts...
              </button>
            </div>
          </Col>
        </Row>
        <Row>
          <Col sm={6} className="p-2 text-end">
            <button
              className="btn btn-outline-light fw-bold border-0 text-dark themeColorUser"
              type="button"
              data-bs-toggle="modal"
              data-bs-target="#staticBackdrop"
            >
              <FcCamera className="icon" /> Photos
            </button>
          </Col>
          <Col sm={6} className="p-2 text-start divider">
            <button
              className="btn btn-outline-light fw-bold border-0 text-dark themeColorUser"
              type="button"
              data-bs-toggle="modal"
              data-bs-target="#ss"
            >
              <FcVideoCall className="icon" /> Videos
            </button>
          </Col>
        </Row>
        <div class="modal fade" id="ss" data-bs-backdrop="static" data-bs-keyboard="false" tabindex="-1" aria-labelledby="staticBackdropLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="staticBackdropLabel">Modal title</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        ...
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Understood</button>
      </div>
    </div>
  </div>
</div>
      </Container>
    </div>
  );
}

export default CreatePost;
