import React, { Fragment, useEffect, useState, useContext } from "react";
import { Helmet, HelmetProvider } from "react-helmet-async";
import { useNavigate } from "react-router-dom";
import Axios from "axios";

//SESSION and INPUT
import { AuthContext } from "../../Helpers/AuthContext";
import useInput from "../../Helpers/use-input";

import { Container, Row, Col, InputGroup, Form } from "react-bootstrap";
import { BiMessageCheck, BiMinusCircle } from "react-icons/bi";
import DatePicker from "react-datepicker";
import { Typeahead } from "react-bootstrap-typeahead";

//Toast
import { toast } from "react-toastify";

//Components
import Heading from "../Heading";

const employerBasicInfoFormSubmissionHandler = (event) => {};

function Employer() {
  const { authState, setAuthState } = useContext(AuthContext);
  const [hearlist, setHearList] = useState([]);
  const [location, setlocation] = useState([]);

  useEffect(() => {
    Axios.get(`${process.env.REACT_APP_SERVER_DOMAIN}/meta/hearZomIN`).then(
      (response) => {
        setHearList(response.data);
      }
    );
  }, []);

  useEffect(() => {
    Axios.get(`${process.env.REACT_APP_SERVER_DOMAIN}/meta/stateLocation`).then(
      (response) => {
        setlocation(response.data);
      }
    );
  }, []);

  const roleClasses = "form-control form-control-sm";
  const hearClasses = "form-select form-select-sm";

  return (
    <AuthContext.Provider value={{ authState, setAuthState }}>
      <Fragment>
        <HelmetProvider>
          <Helmet>
            <title> Basic Info | Zom - IN </title>
          </Helmet>
        </HelmetProvider>
        <div id="basicInfo" className="basicInfo">
          <Container className="my-5">
            <Row>
              <Col sm={6} xs={12} className="mx-auto">
                <Heading content="Employer Info" design="h4 mb-4" />
                <Form onSubmit={employerBasicInfoFormSubmissionHandler}>
                  <Col sm={12} className="form-group mb-2">
                    <label htmlFor="role" className="required">
                      Role
                    </label>
                    <input
                      type="text"
                      placeholder="What's your role in your company?"
                      className={roleClasses}
                      // onChange={roleChangeHandler}
                      // onBlur={roleBlurHandler}
                      // value={enteredRole}
                    />
                  </Col>
                  <Col sm={12} className="form-group mb-2">
                    <label htmlFor="inputabout" className="required">
                      Logo
                    </label>
                    <input
                      type="file"
                      accept="image/*"
                      className={roleClasses}
                      // onChange={imageChange}
                    />
                  </Col>
                  <Col sm={12} className="form-group mb-2">
                    <label htmlFor="location" className="required">
                      Location
                    </label>
                    <select
                      defaultValue={""}
                      aria-label="Location"
                      className={hearClasses}
                      // onChange={highLevelChangeHandler}
                      // onBlur={highLevelBlurHandler}
                    >
                      <option value="">Where is your Company located?</option>
                      {location.map((location, key) => {
                        const enteredLocation = location.child_name;
                        return (
                          <option key={enteredLocation} value={enteredLocation}>
                            {enteredLocation}
                          </option>
                        );
                      })}
                    </select>
                  </Col>
                  <Col sm={12} className="form-group mb-3">
                    <label htmlFor="companyRegNum" className="required">
                      Company Registration Number
                    </label>
                    <input
                      type="text"
                      placeholder="What is your company's registration number?"
                      className={roleClasses}
                      // onChange={roleChangeHandler}
                      // onBlur={roleBlurHandler}
                      // value={enteredRole}
                    />
                  </Col>
                  <Col sm={12} className="form-group mb-2">
                    <label htmlFor="inputabout" className="required">
                      About
                    </label>
                    <Form.Control
                      as="textarea"
                      placeholder="Tell us about your Company"
                      className={roleClasses}
                      // onChange={aboutChangeHandler}
                      // onBlur={aboutBlurHandler}
                      // value={enteredAbout}
                    />
                    {/* {aboutInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Tell us about you.
                      </Form.Control.Feedback>
                    )} */}
                  </Col>
                  <hr className="my-4" />

                  <Col sm={12} className="form-group mb-2">
                    <label htmlFor="eduLevel" className="required">
                      Hear about Zom-IN
                    </label>
                    <select
                      defaultValue={""}
                      aria-label="Education Level"
                      className={hearClasses}
                      // onChange={highLevelChangeHandler}
                      // onBlur={highLevelBlurHandler}
                    >
                      <option value="">How did you hear about Zom-IN?</option>
                      {hearlist.map((hearlist, key) => {
                        const enteredHearList = hearlist.child_name;
                        return (
                          <option key={enteredHearList} value={enteredHearList}>
                            {enteredHearList}
                          </option>
                        );
                      })}
                    </select>
                  </Col>
                  <Col sm={12} className="form-group mb-2">
                    <label htmlFor="eduLevel" className="required">
                      Why you are interest in Zom-IN?
                    </label>
                    <div class="form-check ms-3">
                      <input
                        class="form-check-input"
                        type="checkbox"
                        value=""
                        id="flexCheckDefault"
                      />
                      <label class="form-check-label" for="flexCheckDefault">
                      I want to meet more young talents
                      </label>
                    </div>
                    <div class="form-check ms-3">
                      <input
                        class="form-check-input"
                        type="checkbox"
                        value=""
                        id="flexCheckChecked"
                        checked
                      />
                      <label class="form-check-label" for="flexCheckChecked">
                      I want to empower & influence youths
                      </label>
                    </div>
                    <div class="form-check ms-3">
                      <input
                        class="form-check-input"
                        type="checkbox"
                        value=""
                        id="flexCheckDefault"
                      />
                      <label class="form-check-label" for="flexCheckDefault">
                      I want to increase brand awareness among millennials
                      </label>
                    </div>
                    <div class="form-check ms-3">
                      <input
                        class="form-check-input"
                        type="checkbox"
                        value=""
                        id="flexCheckChecked"
                        checked
                      />
                      <label class="form-check-label" for="flexCheckChecked">
                      <input
                      type="text"
                      placeholder="What is your company's registration number?"
                      className={roleClasses}
                      // onChange={roleChangeHandler}
                      // onBlur={roleBlurHandler}
                      // value={enteredRole}
                    />
                      </label>
                    </div>
                  </Col>
                  <Col sm={12} className="form-group mb-2">
                    <label htmlFor="eduLevel" className="required">
                      Do you have any enquires on TOB?
                    </label>
                    <div class="form-check form-switch ms-3">
                      <input
                        class="form-check-input"
                        type="checkbox"
                        role="switch"
                        id="flexSwitchCheckDefault"
                      />
                      <label
                        class="form-check-label"
                        for="flexSwitchCheckDefault"
                      >
                        Default switch checkbox input
                      </label>
                    </div>
                  </Col>
                  <Col className="text-center my-3">
                    <button
                      type="submit"
                      className=" btn btn-primary mx-1"
                      // disabled={!userBasicInfoFormIsValid}
                    >
                      Submit
                    </button>
                    <button
                      type="submit"
                      className=" btn btn-outline-success mx-1"
                      // disabled={!userBasicInfoFormIsValid}
                    >
                      Submit and I want to be a Partner!
                    </button>
                  </Col>
                </Form>
              </Col>
            </Row>
          </Container>
        </div>
      </Fragment>
    </AuthContext.Provider>
  );
}

export default Employer;
