import React, { Fragment, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Axios from 'axios';
import { BiError, BiMessageCheck } from "react-icons/bi";

//Toast
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Verify() {
    useEffect(() => {

    }, []);
    const { token } = useParams();

    if (token) {
        Axios.post('http://localhost:3001/verify/token', {
            token: token
        })
        .then ((response) => {
            if (response.data === 'Email Activated'){
                return toast.success(
                    <Fragment>
                        <BiMessageCheck />
                        <span>Email Activated</span>
                    </Fragment>
                    
                    );
            } else if (response.data === 'Email Cannot Activate!') {
                return toast.success(
                    <Fragment>
                        <BiMessageCheck />
                        <span>Email Cannot Activate!</span>
                    </Fragment>);
            } else if (response.data === 'Invalid Token!'){
                return toast.success(
                    <Fragment>
                        <BiMessageCheck />
                        <span>Invalid Token!</span>
                    </Fragment>);
            } else {
                return toast.success(
                    <Fragment>
                        <BiMessageCheck />
                        <span>System Error</span>
                    </Fragment>);
            }
        // }).then (setTimeout(()=>{
        //     console.log("hiiii");
        // },5000))
        // .then ((res) =>{
        //     window.location = "/signup";
        //     res.preventDefault();
        });
    }   
     else {
        return toast.error(
            <Fragment>
                <BiError />
                <span>Email sent successfully!</span>
            </Fragment>);
    }

    

    if (token === null) {
        window.location.href="/index";
    }

    return (
<Fragment>
     <ToastContainer
                position="bottom-left"
                autoClose={5000}
                hideProgressBar={false}
                newestOnTop
                closeOnClick
                rtl={false}
                pauseOnFocusLoss
                draggable
                pauseOnHover
                render="/index"
            />
</Fragment>
    );


}

export default Verify;
