import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import UserImg from "../../Assests/Images/user/sha.jpeg";


function CreatePost() {
    return (
        <div className="card py-3">
            <Container>
                <Row>
                    <Col sm={1}>
                        <img src={UserImg} className="d-flex img-fluid my-auto rounded-circle" alt="zom-in user" />
                    </Col>
                    <Col sm={11}>
                        <input type="text" placeholder="Share your thoughts..." className="form-control fw-bold p-3 rounded-pill" />
                    </Col>
                </Row>
            </Container>
        </div>
    )
}

export default CreatePost
