import React from "react";
// { Fragment, useContext } 
// import Axios from "axios";
// import { useNavigate } from "react-router-dom";
// import { AuthContext } from "../../Helpers/AuthContext";

import { Modal } from "react-bootstrap";
// import { BiMessageCheck, BiMinusCircle } from "react-icons/bi";

// //Toast
// import { toast } from "react-toastify";

import Heading from "../Heading";

function JobStatus(props) {

    const grabID = props.grabID;
    const statusCode = props.status;

    let statusInfo;
    let status;
    switch (statusCode) {
      case "0":
        status = "Pending"
        statusInfo = "Pending";
        break;
      case "1":
        status = "Reject"
        statusInfo = "Rejecting";
        break;
      case "2":
        status = "Hired"
        statusInfo = "Hiring";
        break;
        case "3":
          status = "Shortlisted"
        statusInfo = "Shortlisting";
        break;
      default:
        statusInfo = "Pending";
    }

    return (
        <div>
            <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Heading content={`${statusInfo} this candidate?`} design="h5" />
        </Modal.Header>
        <Modal.Body>
          {status === "Reject" && (
            <>
            <Heading content="Reason for Rejection" design="h6 mb-2" />

            <p className="fs-6 mb-2"> Please state your reason for candidate rejection: </p>

            </>
          )}
        </Modal.Body>
        </Modal>
        </div>
    )
}

export default JobStatus
