import React, { useState, useEffect, Fragment } from "react";
import { useNavigate } from "react-router-dom";
import Axios from "axios";
import { BiPencil, BiMinusCircle } from "react-icons/bi";
import LocationIcon from "../../../Assests/Images/icon/location.svg";

import { Col, Row, Modal, Button, Form } from "react-bootstrap";
import Heading from "../../Heading";
import { toast } from "react-toastify";

import useInput from "../../../Helpers/use-input";

const isNotEmpty = (value) => value.trim() !== "";


function About() {
  const navigate = useNavigate();

  const [about, setAbout] = useState([]);
  const [show, setShow] = useState(false);

  const [fullName, setFullName] = useState("");

  useEffect(() => {
    if (!localStorage.getItem("accessToken")) {
      navigate("/signup");
    } else {
      Axios.get(
        `${process.env.REACT_APP_SERVER_DOMAIN}/dashboardUserProfile/about/`,
        {
          headers: {
            accessToken: localStorage.getItem("accessToken"),
          },
        }
      ).then((response) => {
        if (response.data.error) {
          return toast.error(
            <Fragment>
              <BiMinusCircle /> <span>{response.data.error}</span>
            </Fragment>
          );
        } else {
          setAbout(response.data);
        }
      });
    }
  }, []);


    //DOB Input Check
  //   const {
  //     value: enteredName,
  //     isValid: enteredNameIsValid,
  //     hasError: nameInputHasError,
  //     valueChangeHandler: nameChangeHandler,
  //     inputBlurHandler: nameBlurHandler,
  //     reset: resetNameInput,
  //   } = useInput(isNotEmpty);

  // let aboutMeFormIsValid = false;

  // if(enteredNameIsValid) {
  //   aboutMeFormIsValid = true;
  // }

  const aboutMeFormSubmissionHandler = (event) => {
    event.preventDefault();
// console.log(event.target.value)
console.log("hi")
console.log(fullName)
    // if(!enteredNameIsValid) {
    //   return;
    // } else {
    //   alert("sending")
    // }

    // resetNameInput();
  };

  const nameClasses = "form-control form-control-sm";

  // const nameClasses = nameInputHasError
  // ? "form-control form-control-sm is-invalid"
  // : "form-control form-control-sm";

  return (
    <>
      <Row className="mx-auto border rounded p-2 my-2">
        <Col sm={12}>
          <Row>
            <Col xs={10} sm={10} className="text-start">
              <Heading content="About Me" design="h5" />
            </Col>
            <Col xs={2} sm={2} className="text-end align-self-center">
              <p
                role="button"
                className="my-auto"
                // onClick={() => setShow(true)}
              >
                <BiPencil 
                className="icon"
                type="button"
                data-bs-toggle="modal"
                data-bs-target="#about"
                 />
              </p>
            </Col>
          </Row>
        </Col>
        <Col sm={12}>
          <Col>
            {about.map((aboutDetails) => {
              const enteredAbout = aboutDetails.about_user;
              const ok = aboutDetails.name;
              const enteredPhone = aboutDetails.contnum;
              const enteredEmail = aboutDetails.email;
              const enteredGender = aboutDetails.gender;
              const dob = new Date(aboutDetails.dob);
              const enteredDob = dob.toISOString().substr(0, 10);
              const enteredNationality = aboutDetails.nationality;
              const enteredAddress = aboutDetails.user_address;

              return (
                <>
                  { enteredAddress === 'null' &&
                  <p className="m-0 text-start fs-7 text-capitalize">
                    <img src={LocationIcon} className="icon" />
                    {enteredAddress}
                  </p>
                  }
                  <p className="fs-7 text-truncate-3 text-justify">
                    {enteredAbout}
                  </p>







                  <div
            className="modal fade"
            id="about"
            data-bs-backdrop="static"
            data-bs-keyboard="false"
            tabIndex="-1"
            aria-labelledby="staticBackdropLabel"
            aria-hidden="true"
          >
            <div className="modal-dialog ">
              <div className="modal-content">
                <div className=" modal-header">
                  <Heading content="About Me" design="h4 my-auto" />
                  <button
                    type="button"
                    id="closeButton"
                    className="btn-close"
                    data-bs-dismiss="modal"
                    aria-label="Close"
                  ></button>
                </div>
                <Form
                  onSubmit={aboutMeFormSubmissionHandler}
                  encType="multipart/form-data"
                >
                  <div className="modal-body">
                  <Col sm={12} className="form-group mb-3">
                          <label htmlFor="fullName" className="required">
                            Full Name
                          </label>
                          <input
                            type="text"
                            placeholder="Enter your full name"
                            className={nameClasses}
                            onChange={(e) => setFullName(e.target.value)}
                          value={[fullName || ok]}
                            // onChange={nameChangeHandler}
                            // onBlur={nameBlurHandler}
                            name="fname"
                            // defaultValue={ok}
                            // value={enteredName}
                          />

                          {/* {nameInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Please fill in your full name.
                      </Form.Control.Feedback>
                    )} */}
                        </Col>
                    <Col sm={12} className="form-group mb-2">
                      <label htmlFor="experience" className="required">
                        Experience
                      </label>
                    </Col>
                  </div>
                  <div className="modal-footer">
                    <button
                      type="button"
                      className="btn btn-secondary"
                      data-bs-dismiss="modal"
                    >
                      Close
                    </button>
                    <button
                      type="submit"
                      className="btn btn-primary"
                      data-bs-dismiss="modal"
                      // disabled={!workExpFormIsValid}
                    >
                      Save Changes
                    </button>
                  </div>
                </Form>
              </div>
            </div>
          </div>





                </>
              );
            })}
          </Col>
        </Col>
      </Row>
    </>
  );
}

export default About;
