import React from 'react';
import { Col, InputGroup, Form } from "react-bootstrap";

import useInput from "../../Helpers/use-input";

//Components
import Heading from '../Heading';

function User(props) {

  const userBasicInfoFormSubmissionHandler =() => {};

    return (
        <Col sm={6} xs={12} className="mx-auto">
                <h1>{props.id}</h1>
                <Heading content="Basic Info" />
                <Form onSubmit={userBasicInfoFormSubmissionHandler}>

                <Col sm={12} className="form-group mb-3">
                <label htmlFor="inputgender" className="required">
                  You are
                </label>
                <InputGroup>
                  <div>
                    <input
                      type="radio"
                      className="btn-check"
                      name="gender"
                      id="male"
                      autoComplete="off"
                    //   onChange={genderChangeHandler}
                    //   onBlur={genderBlurHandler}
                      value="Male"
                    //   value={enteredGender}
                    />
                    <label className="btn btn-outline-success" htmlFor="male">
                      Male
                    </label>
                  </div>

                  <div className="ms-2 ">
                    <input
                      type="radio"
                      className="btn-check"
                      name="gender"
                      id="female"
                      autoComplete="off"
                    //   onChange={genderChangeHandler}
                    //   onBlur={genderBlurHandler}
                    //   value={enteredGender}
                      value="Female"
                    />
                    <label className="btn btn-outline-info" htmlFor="female">
                      Female
                    </label>
                  </div>
                </InputGroup>
                {/* {genderInputHasError && (
                  <Form.Control.Feedback type="invalid">
                    Please choose your gender.
                  </Form.Control.Feedback>
                )} */}
              </Col>

                </Form>
              </Col>
    )
}

export default User
