import React, { useState, useEffect,Fragment } from 'react';
import { useNavigate } from "react-router-dom";
import Axios from "axios";
import { BiPencil, BiMinusCircle, BiCalendar } from "react-icons/bi";

import { Col, Row } from "react-bootstrap";
import Heading from '../../../Heading';
import { toast } from "react-toastify";



function Achievement() {
  const navigate = useNavigate();


  const [accomplishment, setAccomplishment] = useState([]);

  useEffect(() => {
    if (!localStorage.getItem("accessToken")) {
      navigate("/signup");
    } else {
    Axios.get(`${process.env.REACT_APP_SERVER_DOMAIN}/dashboardUserProfile/accomplishment/`,
    {
      headers: {
        accessToken: localStorage.getItem("accessToken"),
      },
    }
    ).then(
      (response) => {
        if (response.data.error) {
            return toast.error(
              <Fragment>
                <BiMinusCircle /> <span>{response.data.error}</span>
              </Fragment>
            );
          } else {
            setAccomplishment(response.data);
          }

      }
    );
  }
  }, []);

    return (
        <Row className="mx-auto border rounded p-2 my-2">
        <Col sm={12}>
         <Row>
         <Col xs={10} sm={10} className="text-start">
            <Heading content="Awards and Achievements" design="h5" />
          </Col>
          <Col xs={2} sm={2} className="text-end align-self-center">
            <p role="button" className="my-auto"><BiPencil className="icon" /></p>
          </Col>
         </Row>
        </Col>
        <Col sm={12}>
          <Col>
          {accomplishment.map((accomplishmentDetails, key) => {
                  const title = accomplishmentDetails.accomplishment;
                  const desc = accomplishmentDetails.desc;
                  const date_accomplishment = new Date(accomplishmentDetails.date_accomplishment);
                  const date = date_accomplishment.toLocaleDateString("en-GB", {
                      month: "short",
                      year: "numeric"
                      }).replace(/ /g, " ");

                  return (
                      <div className="mb-2">
                      <Row>
                <Col lg={8} sm={12}>
                  <p className="text-start m-0 fw-bold fs-6">{title}</p>
                </Col>
              </Row>
              <Row>
              <p className="m-0 text-start fs-7"><BiCalendar className="icon"/> {date} </p>
                <p className="m-0 text-start fs-7">
                  {desc}
                </p>
              </Row>
              </div>

                  )
              })}
          </Col>
        </Col>
      </Row>
    )
}

export default Achievement
