import React, { Fragment, useState, useEffect } from 'react';
import { Helmet } from 'react-helmet';
import Axios from 'axios';

import Header from '../Components/Header';

function SignUp() {

    const [userIsRegistered, setLogin] = useState(false);

    function getLogin() {
        setLogin(!userIsRegistered);
    }

    const [fullName, setFullName] = useState("");
    const [email, setEmail] = useState("");
    const [contactNumber, setContactNumber] = useState("");
    const [password, setPassword] = useState("");

    const [signUpStatus, setSignUpStatus] = useState("");

    useEffect(() => {

    }, []);

    const handleChange = (event) => {
        //Set the value of tweet
        setFullName(event && event.target.value ? event.target.value : "");
    }


    const save = (event) => {
        if (fullName && email && password && contactNumber) {
            Axios.post('http://localhost:3001/auth/signup', {
                fullName: fullName,
                email: email,
                contactNumber: contactNumber,
                password: password
            })
                .then(async (res) => {
                    if (res.data.message) {
                        setSignUpStatus(res.data.message);
                    } else {
                        setSignUpStatus(res.data[0].username);
                    }

                    event.preventDefault();
                })
        }
    }

    return (
        <Fragment>
            <Helmet>
                <title>Sign Up</title>
            </Helmet>
            <Header />
            <div id="signUp" className="col-11 col-md-9 text-center">
                <div className="row">
                    <div className="col-12 col-md-6">
                        {userIsRegistered ?

                            <LogInForm />

                            :
                            <div>
                                <Heading content="Sign Up Now!" />
                                <form id="signUpForm" >
                                    <div className="mb-3">
                                        <input className="form-control form-control-sm" type="text" name="fullName" placeholder="Full Name" value={fullName} required
                                            onChange={handleChange} />
                                    </div>
                                    <div className="mb-3">
                                        <input className="form-control form-control-sm" type="email" name="email" placeholder="Email Addresss" value={email} required
                                            onChange={(e) => {
                                                setEmail(e.target.value);
                                            }} />
                                    </div>
                                    <div className="mb-3">
                                        <input className="form-control form-control-sm" type="num" name="contactNumber" placeholder="Contact Number" value={contactNumber} required
                                            onChange={(e) => {
                                                setContactNumber(e.target.value);
                                            }} />
                                    </div>
                                    <div className="mb-3">
                                        <input className="form-control form-control-sm" type="password" name="password" placeholder="Password" value={password} required
                                            onChange={(e) => {
                                                setPassword(e.target.value);
                                            }} />
                                    </div>

                                    <div className="mb-3">
                                        <label>By clicking sign up, you agree to Zom-In's <a className="text-decoration-none" href="https://github.com" >Privacy Policy</a> and <a className="text-decoration-none" href="https://github.com">Term of Use</a></label>
                                    </div>

                                    <div className="col-12 mb-3">
                                        <button className="btn btn-primary" onClick={save}>Sign Up</button>
                                    </div>
                                </form>
                                <h1>{signUpStatus}</h1>

                            </div>

                        }

                        <div className="row">
                            <div className="col">
                                <div className=" text-start">
                                    <AnchorParagraph link="#" paragraphContent={userIsRegistered ? "Forget Password?" : null} />
                                </div>
                            </div>
                            <div className="col">
                                <div className=" text-end">
                                    <AnchorParagraph link="#" paragraphContent={userIsRegistered ? "Don't have an account?" : "Already have an account?"} onCLickLink={getLogin} />
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="col-12 col-md-6 divider ">
                        <Heading content={userIsRegistered ? "Log In with Social Network" : "Sign Up with Social Network"} />
                        <div className="mb-5 d-grid gap-3">
                            <AnchorIcon link="/employer" btnClass="btn btn-success" btnIcon="bi bi-briefcase-fill" btnName={userIsRegistered ? "Log In as Employer" : "Sign Up as Employer"} />
                        </div>
                        <div className="mb-5 d-grid gap-3">
                            <AnchorIcon link="/google" btnClass="btn btnGoogle" btnIcon="bi bi-google" btnName={userIsRegistered ? "Log In with Google" : "Sign Up with Google"} />
                        </div>
                        <div className="mb-5 d-grid gap-3">
                            <AnchorIcon link="/facebook" btnClass="btn btnFacebook" btnIcon="bi bi-facebook" btnName={userIsRegistered ? "Log In with Facebook" : "Sign Up with Facebook"} />
                        </div>
                    </div>
                </div>
            </div>
        </Fragment>
    )
}

export default SignUp
