import React, { useEffect, useState} from 'react';
import Axios from 'axios';
import { Container, Col } from "react-bootstrap";


function Comment(props) {

  const userID = props.commentOwnerID;
    const [viewName, setViewName] = useState("avatar/male.svg");

    useEffect(() => {
      Axios.get(
        `${process.env.REACT_APP_SERVER_DOMAIN}/home/userName/` + userID
      ).then((response) => {
        const userName = response.data[0];
        setViewName(userName.name);
      });
    }, [userID]);
  return (
    <Container>
       <Col xs={1} sm={1} className="p-0 ps-2">
              <h1>{viewName}</h1>
            </Col>
          </Container>
  )
}

export default Comment
