import React, { useEffect, useState } from "react";
import Axios from "axios";

// import TopBanner from "./TopBanner";
import Profile from "./Profile";
import Suggestion from "./Suggestion";
import CreatePost from "./CreatePost";
import MiddleBanner from "./MiddleBanner";
import Posts from "./Posts";
import Sidebar from "./Sidebar";
import ScrollToTop from "../ScrollToTop";

import { Container, Row, Col } from "react-bootstrap";

//Toast
import { toast } from "react-toastify";

function Home(props) {
  const [postList, setPostList] = useState([]);

  useEffect(() => {
    Axios.get(`${process.env.REACT_APP_SERVER_DOMAIN}/home/postList`).then(
      (response) => {
        setPostList(response.data);
      }
    );
  }, []);

  const updatePost = () => {
      window.location.reload();
  }

  const deletePost =(event) => {
    const deletePostID = event.target.value;

    Axios.patch(
      `${process.env.REACT_APP_SERVER_DOMAIN}/home/deletePost/` + deletePostID
    ).then ((response) => {
      if (response.data.error) {
        return toast.error(
          <Fragment>
            <BiMinusCircle /> <span>{response.data.error}</span>
          </Fragment>
        );
      } else {
        return [toast.success(
          <Fragment>
            <BiMessageCheck /> <span>{response.data.success}</span>
          </Fragment>
        ), window.location.reload()]
      }

    })
  }

  return (
    <div id="home" className="home my-2">
      <Container className="mx-auto mb-5">
        <Row>{/* <TopBanner /> */}</Row>
      </Container>
      <Container className="my-2">
        <Row>
          <Col
            lg={2}
            className="d-none d-xs-none d-sm-none d-md-none d-lg-block"
          >
            <Profile userID={props.userID} />
            <Suggestion />
          </Col>
          <Col sm={7} md={9} lg={7}>
            <CreatePost userID={props.userID} updatePost={updatePost}  />
            <MiddleBanner />

            {postList.map((postDetails, key) => {
              const postID = postDetails.post_id;
              const postText = postDetails.post_text;
              const postImageName = postDetails.post_image;
              const postVideoName = postDetails.post_video;
              const postOwnerID = postDetails.owner_user_id;
              const postUserName = postDetails.name;
              const postingTime = new Date(postDetails.time);
              const postCompany = postDetails.company;
              const postCompanyName = postDetails.companyname;

              const postTime = postingTime
                .toLocaleDateString("en-GB", {
                  day: "numeric",
                  month: "short",
                  year: "numeric",
                  hour: "2-digit",
                  minute: "2-digit",
                  hour12: true,
                })
                .replace(/ /g, " ");

              const postImage =
                `${process.env.REACT_APP_SERVER_DOMAIN}/public/Assests/Images/post/` +
                postImageName;
              const postVideo =
                `${process.env.REACT_APP_SERVER_DOMAIN}/public/Assests/Videos/post/` +
                postVideoName;

              const sharePostLink = "https://www.zom-in.com/#" + postID;

              return (
                <Posts
                  userID={props.userID}
                  key={postID}
                  postID={postID}
                  postText={postText}
                  postImage={postImage}
                  postImageName={postImageName}
                  postVideo={postVideo}
                  postVideoName={postVideoName}
                  postOwnerID={postOwnerID}
                  postUserName={postUserName}
                  postTime={postTime}
                  postCompany={postCompany}
                  postCompanyName={postCompanyName}
                  sharePostLink={sharePostLink}
                />
              );
            })}
          </Col>
          <Col
            sm={3}
            md={3}
            lg={3}
            className="d-none d-md-block d-lg-block d-xl-block"
          >
            <Sidebar />
          </Col>
        </Row>
      </Container>
      <ScrollToTop />
    </div>
  );
}

export default Home;
