import React, { Fragment, useEffect, useContext, useState } from "react";
import { useNavigate } from "react-router-dom";
import { Helmet, HelmetProvider } from "react-helmet-async";

import { AuthContext } from "../Helpers/AuthContext";
import { Container, Row, Col } from "react-bootstrap";
import Heading from "../Components/Heading";

import { BiChevronRight, BiChevronLeft } from "react-icons/bi";

function Settings() {
  const { authState, setAuthState } = useContext(AuthContext);

  const navigate = useNavigate();
  useEffect(() => {
    if (!localStorage.getItem("accessToken")) {
      navigate("/signup");
    }
  });

  const [showSubContent, setshowSubContent] = useState(1);
  const subContentStatus = (currentSubContent) => {
    setshowSubContent(currentSubContent);
    console.log(showSubContent);
  }
let dd;
  if(window.innerWidth < 426) {
      if(showSubContent > 0) {
        dd = 'd-none';
      } else {
          dd = "d-block"
      }
  } else {
      console.log("bye")
  }

  function showPageContent() {}

  function hidePageContent() {}

  return (
    <AuthContext.Provider value={{ authState, setAuthState }}>
      <Fragment>
        <HelmetProvider>
          <Helmet>
            <title> Settings | Zom - IN </title>
          </Helmet>
        </HelmetProvider>
        <div id="settings" className="settings my-2">
          <Container>
            <Row>
              {/* {showSubContent === 0 &&  */}
              <Col lg={2} md={3} sm={12} className={`mb-4 ${dd}`}> 
                <Row className="d-flex justify-content-between border-bottom"
                  onClick={(e) => subContentStatus(1)}>
                <Col
                    xs={10}
                    sm={10}
                    className="align-self-center fw-bold my-3"

                  >
                    Password
                  </Col>
                  <Col xs={2} sm={2} className="align-self-center">
                    <BiChevronRight className="text-end d-block" />
                  </Col>
                </Row>

                <Row className="d-flex justify-content-between border-bottom"
                  onClick={(e) => subContentStatus(2)}>
                <Col
                    xs={10}
                    sm={10}
                    className="align-self-center fw-bold my-3"

                  >
                    second option
                  </Col>
                  <Col xs={2} sm={2} className="align-self-center">
                    <BiChevronRight className="text-end d-block" />
                  </Col>
                </Row>
              </Col>
              {/* } */}

              <Col lg={8} md={9} sm={12} className="" >
{showSubContent === 1 ? 
<h1><BiChevronLeft className="me-2 d-block d-sm-none" onClick={(e) => subContentStatus(0)}/>1---bbbbb</h1>
 : 
 <h1>not 1 aaaaaa</h1>
 }
              </Col>
              <Col lg={2} className="d-none d-md-none d-lg-block border">
                 <Heading content="Ads" design="h5" />
              </Col>

            </Row>
          </Container>
        </div>
      </Fragment>
    </AuthContext.Provider>
  );
}

export default Settings;
