import React, { Fragment, useState, useEffect } from "react";
import { Helmet, HelmetProvider } from "react-helmet-async";
import Axios from "axios";
import useInput from "../Hooks/use-input";

// import DatePicker from "react-datepicker";
import { Typeahead } from "react-bootstrap-typeahead";
import { Container, Col, Row, InputGroup, Form } from "react-bootstrap";

//Componnets
import Header from "../Components/Header";
import Heading from "../Components/Heading";

const isNotEmpty = (value) => value.trim() !== "";

function BasicInfo(props) {
  // Gender Input Check
  const {
    value: enteredGender,
    isValid: enteredGenderIsValid,
    hasError: genderInputHasError,
    valueChangeHandler: genderChangeHadler,
    inputBlurHandler: genderBlurHandler,
    reset: resetGenderInput,
  } = useInput(isNotEmpty);

  //DOB Input Check
  const {
    value: enteredDob,
    isValid: enteredDobIsValid,
    hasError: dobInputHasError,
    valueChangeHandler: dobChangeHadler,
    inputBlurHandler: dobBlurHandler,
    reset: resetDobInput,
  } = useInput(isNotEmpty);

  //About Input Check
  const {
    value: enteredAbout,
    isValid: enteredAboutIsValid,
    hasError: aboutInputHasError,
    valueChangeHandler: aboutChangeHadler,
    inputBlurHandler: aboutBlurHandler,
    reset: resetAboutInput,
  } = useInput(isNotEmpty);

    //School Input Check
    // const {
    //   value: enteredSchool,
    //   isValid: enteredSchoolIsValid,
    //   hasError: schoolInputHasError,
    //   valueChangeHandler: schoolChangeHadler,
    //   inputBlurHandler: schoolBlurHandler,
    //   reset: resetSchoolInput,
    // } = useInput(isNotEmpty);

  let basicInfoFormIsValid = false;

  if (enteredGenderIsValid && enteredDobIsValid && enteredAboutIsValid ) {
    basicInfoFormIsValid = true;
  }

  const formSubmissionHandler = (event) => {
    event.preventDefault();

    if (!enteredGenderIsValid && !enteredDobIsValid && !enteredAboutIsValid) {
      return;
    }

    console.log("Can Submit to BE");
    console.log(enteredGender);
    console.log(enteredDob);
    console.log(enteredAbout);
    // console.log(enteredSchool);
    resetGenderInput();
    resetDobInput();
    resetAboutInput();
    // resetSchoolInput();
  };

  const dobClasses = dobInputHasError
    ? "form-control is-invalid"
    : "form-control";

  const aboutClasses = aboutInputHasError
    ? "form-control is-invalid"
    : "form-control";



  // School Suggestion
  const [schoolSelections, setSchoolSelections] = useState([]);
  const [universityList, setUniversityList] = useState([]);
  useEffect(() => {
    Axios.get("http://localhost:3001/meta/university").then((response) => {
      setUniversityList(response.data);
    });
  }, []);

  return (
    <Fragment>
      <Header />
      <HelmetProvider>
        <Helmet>
          <title> Basic Info | Zom - IN </title>
        </Helmet>
      </HelmetProvider>

      <Container className="my-5">
        <Row>
          <Col sm={6} xs={12} className="mx-auto">
            <Heading content="Basic Info" />
            <Form onSubmit={formSubmissionHandler}>
              {/* Gender */}
              <Col sm={12} className="form-group mb-3">
                <label htmlFor="inputgender" className="required">
                  You are
                </label>
                <InputGroup>
                  <div>
                    <input
                      type="radio"
                      className="btn-check"
                      name="gender"
                      id="male"
                      autoComplete="off"
                      onChange={genderChangeHadler}
                      onBlur={genderBlurHandler}
                      value="Male"
                      value={enteredGender}
                    />
                    <label className="btn btn-outline-success" htmlFor="male">
                      Male
                    </label>
                  </div>

                  <div className="ms-2 ">
                    <input
                      type="radio"
                      className="btn-check"
                      name="gender"
                      id="female"
                      autoComplete="off"
                      onChange={genderChangeHadler}
                      onBlur={genderBlurHandler}
                      value={enteredGender}
                      value="Female"
                    />
                    <label className="btn btn-outline-info" htmlFor="female">
                      Female
                    </label>
                  </div>
                </InputGroup>
                {genderInputHasError && (
                  <Form.Control.Feedback type="invalid">
                    Please choose your gender.
                  </Form.Control.Feedback>
                )}
              </Col>

              {/* DAte of Birth */}
              <Col sm={12} className="form-group mb-3">
                <label htmlFor="inputdob" className="required">
                  Date of Birth
                </label>
                <input
                  type="date"
                  id="inputdob"
                  placeholder="Your Birthday"
                  className={dobClasses}
                  onChange={dobChangeHadler}
                  onBlur={dobBlurHandler}
                  value={enteredDob}
                />
                {dobInputHasError && (
                  <Form.Control.Feedback type="invalid">
                    Please select your birthdate.
                  </Form.Control.Feedback>
                )}
              </Col>

              {/* About Me */}
              <Col sm={12} className="form-group mb-2">
                <label htmlFor="inputabout" className="required">
                  About
                </label>
                <Form.Control
                  as="textarea"
                  placeholder="Tell us about you"
                  className={aboutClasses}
                  onChange={aboutChangeHadler}
                  onBlur={aboutBlurHandler}
                  value={enteredAbout}
                />
                {aboutInputHasError && (
                  <Form.Control.Feedback type="invalid">
                    Tell us about you.
                  </Form.Control.Feedback>
                )}
              </Col>
              <hr className="my-4" />

              <Heading content="Education" />
              <Col sm={12} className="form-group mb-2">
                <label htmlFor="inputSchool" className="required">School</label>

                <Typeahead
                  id="basic-typeahead-single"
                  onChange={setSchoolSelections}
                  labelKey={option => `${option.university_name}`}
                  options={universityList}
                  placeholder="Choose a state..."
                  selected={schoolSelections}
                  // className={schoolClasses}
                  // onChange={schoolChangeHadler}
                  // onBlur={schoolBlurHandler}
                  // value={enteredSchool}
                />
              </Col>

              <Col className="text-end my-3">
                <button
                  type="submit"
                  className=" btn btn-primary"
                  disabled={!basicInfoFormIsValid}
                >
                  Submit
                </button>
              </Col>
            </Form>
          </Col>
        </Row>
      </Container>
    </Fragment>
  );
}

export default BasicInfo;
