import React, { Fragment } from "react";
import { Helmet, HelmetProvider } from "react-helmet-async";

import { Col, Container, Row } from "react-bootstrap";
import { BiSearchAlt } from "react-icons/bi";
import Heading from "../Components/Heading";


function Faq() {
  return (
    <Fragment>
      <HelmetProvider>
        <Helmet>
          <title> FAQs | Zom - IN </title>
        </Helmet>
      </HelmetProvider>
      <div id="faq" className="faq">
          {/* <Container>
              <Row className="d-flex">
              <Col lg={10} md={9} sm={12} className="mx-auto">
                  <Row className="justify-content-center">

                  </Row>
              </Col>
              </Row>
          </Container> */}



          <Container fluid className="h-100 searchbox">
          <Row className="py-3 px-5 text-center mx-auto">
          <Heading content="How can we help you?" design="h4 fw-bold" />
          {/* <div class="fs-3 fw-bold"></div> */}

          <div className="input-group ">

                        <input
                            type="text"
                            className="form-control"
                            placeholder="Describe the issue here"
                            aria-label="Keywords"
                            aria-describedby="button-addon2"
                            // onChange={(event) => {
                            //     setSearchTerm(event.target.value);
                            // }}
                        />
                        <button
                            className="btn btn-outline-primary"
                            type="button"
                            id="button-addon2" >
                            < BiSearchAlt /> Search
                        </button>
                    </div>
                    <div className="mx-2">
                                <p>
                                    You do not find the answer you want?
                                    <a href="contact-us.html" class="text-decoration-none">Click here</a>
                                    to contact us.
                                </p>
                            </div>
          </Row>
          </Container>
      </div>
    </Fragment>
  );
}

export default Faq;
