import React from 'react';
import { Row, Col } from "react-bootstrap";

import UserImg from "../../Assests/Images/user/sha.jpeg";
import {
    BsClockFill
  } from "react-icons/bs";


function Comment(props) {
    return (
        <Row className=" py-3">
            <Col xs={1} sm={1} className="p-0 ps-2">
              <img
                src={UserImg}
                alt="eger"
                className=" col w-100 rounded-circle"
              />
            </Col>



            <Col xs={11} sm={11}>
              <div className="p-2 border rounded bg-light bg-gradient">
                <p>
                  {props.comment}
                </p>
              </div>
              <p className="text-muted fw-light fs-7">
                <BsClockFill size={12} className="me-1" />
                10th June 2021 5:14 pm
              </p>
            </Col>
          </Row>
    )
}

export default Comment
