import React, { Fragment, useEffect, useContext } from 'react';
import { useNavigate } from "react-router-dom";
import { Helmet, HelmetProvider } from "react-helmet-async";

import { AuthContext } from "../Helpers/AuthContext";
import { Container, Row, Col } from 'react-bootstrap';
import Heading from '../Components/Heading';


function Settings() {

  const { authState, setAuthState } = useContext(AuthContext);


    const navigate = useNavigate();
    useEffect(() => {
        if (!localStorage.getItem("accessToken")) {
          navigate("/signup");
        }
      });

    return (
    <AuthContext.Provider value={{ authState, setAuthState }}>

        <Fragment>
      <HelmetProvider>
        <Helmet>
          <title> Settings | Zom - IN </title>
        </Helmet>
      </HelmetProvider>
      <div id="settings" class="settings my-2">
      <Container>
          <Row>
              <Col lg={2} md={3} sm={12} className="mb-4 d-none d-md-block">
              <Heading content="Menu" design="h5 fw-bold lh-sm mb-4" />
              <div className="nav text-start flex-column" id="settingsTab" role="tablist">
              <a class="active text-start text-dark text-decoration-none my-2" id="changePassContent" data-bs-toggle="tab" data-bs-target="#changePassTab" type="button" role="tab">Change Password</a>
              <a class="text-start text-dark text-decoration-none my-2" id="feature1Content" data-bs-toggle="tab" data-bs-target="#feature1Tab" type="button" role="tab">Feature 1</a>
              </div>

              </Col>
          </Row>
      </Container>
        </div>
        </Fragment>
    </AuthContext.Provider>
    )
}

export default Settings
