import React, { Fragment, useEffect, useContext, useState } from "react";
import { useNavigate } from "react-router-dom";
import { Helmet, HelmetProvider } from "react-helmet-async";
import Axios from "axios";
import { Container, Row, Col } from "react-bootstrap";

import Heading from "../Components/Heading";
import SubMenu from "../Components/Dashboard/SubMenu";
import UserProfile from "../Components/Dashboard/UserProfile";
import EmployerProfile from "../Components/Dashboard/EmployerProfile";
import EmployerDashboard from "../Components/Dashboard/EmployerDashboard";
import EmployerApplication from "../Components/Dashboard/EmployerApplication";

import { AuthContext } from "../Helpers/AuthContext";

import '../Styles/dashboard.css';


function Dashboard() {
  const navigate = useNavigate();

  const { authState, setAuthState } = useContext(AuthContext);

  useEffect(() => {
    if (!localStorage.getItem("accessToken")) {
      navigate("/signup");
    }
  });

  const [showSubContent, setshowSubContent] = useState(1);
  const subContentStatus = (currentSubContent) => {
    setshowSubContent(currentSubContent);
  };


  let mobileStatus;
  if (window.innerWidth < 575) {
    if (showSubContent > 0) {
        mobileStatus = "d-none";
    } else {
        mobileStatus = "d-block";
    }
  }

  return (
    <AuthContext.Provider value={{ authState, setAuthState }}>
      <Fragment>
        <HelmetProvider>
          <Helmet>
            <title> Dashboard | Zom - IN </title>
          </Helmet>
        </HelmetProvider>
      </Fragment>
      <div id="dashboard" className="dashboard my-2">
        <Container>
          <Row>
            <Col lg={2} md={3} sm={12} className={`mb-4 ${mobileStatus}`}>

            {/* MY DASHBOARD */}
              <Row
                className="d-flex justify-content-between border-bottom"
                onClick={(e) => subContentStatus(1)}
                role="button"
              >
                <SubMenu content="My Dashboard" />
              </Row>

            {/* MY PROFILE */}
            <Row
                className="d-flex justify-content-between border-bottom"
                onClick={(e) => subContentStatus(2)}
                role="button"
              >
                <SubMenu content="My Profile" />
              </Row>


              {/* APPLICATION HISTORY */}
              <Row
                className="d-flex justify-content-between border-bottom"
                onClick={(e) => subContentStatus(3)}
                role="button"
              >
                <SubMenu content="Application History" />
              </Row>

              {/* INTERVIEW SCHEDULE */}
              <Row
                className="d-flex justify-content-between border-bottom"
                onClick={(e) => subContentStatus(4)}
                role="button"
              >
                <SubMenu content="Interview Schedule" />
              </Row>

            </Col>


            <Col lg={8} md={9} sm={12} className={!mobileStatus}>
            {showSubContent === 1 && ( 
                  authState.company === 1 ?
                    <EmployerDashboard  showSubContent={showSubContent}
                  setshowSubContentClick={setshowSubContent} />

                   :
                   <UserProfile
                  showSubContent={showSubContent}
                  setshowSubContentClick={setshowSubContent}
                />
              )}

              {showSubContent === 2 && ( 
                  authState.company === 1 ?
                    <EmployerProfile  showSubContent={showSubContent}
                  setshowSubContentClick={setshowSubContent} />

                   :
                   <UserProfile
                  showSubContent={showSubContent}
                  setshowSubContentClick={setshowSubContent}
                />
              )}

              {showSubContent === 3 && ( 
                  authState.company === 1 ?
                    <EmployerApplication  showSubContent={showSubContent}
                  setshowSubContentClick={setshowSubContent} />

                   :
                   <UserProfile
                  showSubContent={showSubContent}
                  setshowSubContentClick={setshowSubContent}
                />
              )}
            </Col>
            <Col lg={2} className="d-none d-md-none d-lg-block border">
              <Heading content="Ads" design="h5" />
            </Col>
          </Row>
        </Container>
      </div>
    </AuthContext.Provider>
  );
}

export default Dashboard;
