import React, { useState, useEffect,Fragment } from 'react';
import { useNavigate } from "react-router-dom";
import Axios from "axios";
import { BiPencil, BiMinusCircle } from "react-icons/bi";

import { Col, Row } from "react-bootstrap";
import Heading from '../../../Heading';
import { toast } from "react-toastify";



function Portfolio() {
  const navigate = useNavigate();


  const [portfolio, setPortfolio] = useState([]);

  useEffect(() => {
    if (!localStorage.getItem("accessToken")) {
      navigate("/signup");
    } else {
    Axios.get(`${process.env.REACT_APP_SERVER_DOMAIN}/dashboardUserProfile/portfolio/`,
    {
      headers: {
        accessToken: localStorage.getItem("accessToken"),
      },
    }
    ).then(
      (response) => {
        if (response.data.error) {
            return toast.error(
              <Fragment>
                <BiMinusCircle /> <span>{response.data.error}</span>
              </Fragment>
            );
          } else {
            setPortfolio(response.data);
          }

      }
    );
  }
  }, []);

    return (
        <Row className="mx-auto border rounded p-2 my-2">
        <Col sm={12}>
         <Row>
         <Col xs={10} sm={10} className="text-start">
            <Heading content="Portfolio and Projects" design="h5" />
          </Col>
          <Col xs={2} sm={2} className="text-end align-self-center">
            <p role="button" className="my-auto"><BiPencil className="icon" /></p>
          </Col>
         </Row>
        </Col>
        <Col sm={12}>
          <Col>
          {portfolio.map((portfolioDetails, key) => {
                  const portfolioName = portfolioDetails.name_portfolio;
                  const portfolioSummary = portfolioDetails.summary_portfolio;
                  const portfolioLink = portfolioDetails.link_portfolio;
                  const portfolio_date = new Date(portfolioDetails.date_portfolio);

                  const portfolioDate = portfolio_date.toLocaleDateString("en-GB", {
                      month: "short",
                      year: "numeric"
                      }).replace(/ /g, " ");

                  return (
                      <div className="mb-2">
                      <Row>
                <Col lg={8} sm={12}>
                  <p className="text-start m-0 fw-bold fs-6 text-capitalize">{portfolioName}</p>
                </Col>
                <Col lg={4}>
                  <p className="text-lg-end text-start m-0 text-secondary fs-7">
                    {portfolioDate}
                  </p>
                </Col>
              </Row>
              <Row>
                <p className="m-0 text-start fs-7">
                  {portfolioSummary}
                </p>
                <p className="m-0 text-start fs-7">
                  <a className="text-decoration-none" target="_blank" href={portfolioLink}>
                    Project Link{" "}
                  </a>
                </p>
              </Row>
              </div>
                  )
              })}
          </Col>
        </Col>
      </Row>
    )
}

export default Portfolio
