import React from "react";
// { Fragment, useContext } 
// import Axios from "axios";
// import { useNavigate } from "react-router-dom";
// import { AuthContext } from "../../Helpers/AuthContext";

import { Modal } from "react-bootstrap";
// import { BiMessageCheck, BiMinusCircle } from "react-icons/bi";

// //Toast
// import { toast } from "react-toastify";

import Heading from "../Heading";

function JobStatus(props) {

    const grabID = props.grabID;
    const status = props.status;

    let statusInfo;
    switch (status) {
      case "0":
        statusInfo = "Pending";
        break;
      case "1":
        statusInfo = "Rejected";
        break;
      case "2":
        statusInfo = "Hired";
        break;
        case "3":
        statusInfo = "Shortlisted";
        break;
        case "4":
        statusInfo = "Withdrew";
        break;
      default:
        statusInfo = "bg-fullTime";
    }

    return (
        <div>
            <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Heading content={`Applying for ${grabID}, here ${statusInfo}`} design="h5" />
        </Modal.Header>
        </Modal>
        </div>
    )
}

export default JobStatus
