import React, { useState } from "react";
import { Container, Row, Col, Form } from "react-bootstrap";
import UserImg from "../../Assests/Images/user/sha.jpeg";

import { FcCamera, FcVideoCall } from "react-icons/fc";
import { BsXCircleFill } from "react-icons/bs";

import useInput from "../../Helpers/use-input";


const isNotEmpty = (value) => value.trim() !== "";

function CreatePost() {
  const [selectedImage, setSelectedImage] = useState();
  //   let createPostFormIsValid = false;

   //About Input Check
   const {
    value: enteredAbout,
    isValid: enteredAboutIsValid,
    hasError: aboutInputHasError,
    valueChangeHandler: aboutChangeHandler,
    inputBlurHandler: aboutBlurHandler,
    reset: resetAboutInput,
  } = useInput(isNotEmpty);

  // This function will be triggered when the file field change
  const imageChange = (event) => {
    if (event.target.files && event.target.files.length > 0) {
      setSelectedImage(event.target.files[0]);
      //   createPostFormIsValid = true;
    }

    const imageFile = event.target.files[0];

    if (!imageFile.name.match(/\.(jpg|jpeg|png|gif)$/)) {

      return;
    }
  };

  const aboutClasses = aboutInputHasError
  ? "form-control form-control-sm is-invalid"
  : "form-control form-control-sm";

  const removeSelectedImage = () => {
    setSelectedImage();
  };

  const createPostFormSubmissionHandler = (event) => {
    event.preventDefault();
    alert("okay");
};

  return (
    <div className="card py-3">
      <Container>
        <Row className="mx-auto my-2">
          <Col sm={1} className="d-flex">
            <img
              src={UserImg}
              className="img-fluid my-auto rounded-circle"
              alt="zom-in user"
            />
          </Col>
          <Col sm={11}>
            <div class="d-grid gap-2 createPost">
              <button
                class="btn btn-outline-secondary rounded-pill text-start p-3 fw-bold"
                type="button"
                data-bs-toggle="modal"
                data-bs-target="#staticBackdrop"
              >
                Share your thoughts...
              </button>
            </div>
          </Col>
        </Row>
        <Row>
          <Col sm={6} className="p-2 text-end">
            <button
              class="btn btn-outline-light fw-bold border-0 text-dark themeColorUser"
              type="button"
              data-bs-toggle="modal"
              data-bs-target="#staticBackdrop"
            >
              <FcCamera className="icon" /> Photos
            </button>
          </Col>
          <Col sm={6} className="p-2 text-start divider">
            <button
              class="btn btn-outline-light fw-bold border-0 text-dark themeColorUser"
              type="button"
              data-bs-toggle="modal"
              data-bs-target="#staticBackdrop"
            >
              <FcVideoCall className="icon" /> Videos
            </button>
          </Col>
        </Row>
        <div
          class="modal fade"
          id="staticBackdrop"
          data-bs-backdrop="static"
          data-bs-keyboard="false"
          tabindex="-1"
          aria-labelledby="staticBackdropLabel"
          aria-hidden="true"
        >
          <div class="modal-dialog">
            <div class="modal-content">
            <Form onSubmit={createPostFormSubmissionHandler}>
              <div class="modal-body mt-3 mb-0 mx-0 p-0">
                <div className="position-relative">
                  <div class="position-absolute top-0 end-0 p-2">
                    <button
                      type="button"
                      class="btn-close"
                      data-bs-dismiss="modal"
                      aria-label="Close"
                    ></button>
                  </div>
                </div>

                <textarea
                  className=" form-control w-100 border-0 p-4 fw-bold inputPost"
                  placeholder="Share your thoughts..."
                  className={aboutClasses}
                    onChange={aboutChangeHandler}
                    onBlur={aboutBlurHandler}
                    value={enteredAbout}
                ></textarea>

{aboutInputHasError && (
                    <Form.Control.Feedback type="invalid">
                      Tell us about you.
                    </Form.Control.Feedback>
                  )}

                {selectedImage && (
                  <div className="m-2 w-75 mx-auto border border-5 themeColorUser rounded imgPostPreview">
                    <div className="position-relative">
                      <div class="position-absolute top-0 start-100 translate-middle">
                        <button
                          onClick={removeSelectedImage}
                          className="bg-transparent rounded-circle border-0 position-relative"
                        >
                          <BsXCircleFill
                            size={21}
                            className="bg-white rounded-circle"
                          />
                        </button>
                      </div>
                    </div>
                    <img
                      src={URL.createObjectURL(selectedImage)}
                      className="w-100"
                      alt="Thumb"
                    />
                  </div>
                )}
                <Row className="p-3">
                  <Col sm={6} className="p-2 text-end">
                    <span class=" badge rounded-pill fw-bold border-0 text-dark p-3 fs-6 themeColorUser inputPostFile">
                      <FcCamera className="icon" /> Photos
                      <input type="file" onChange={imageChange} />
                    </span>
                  </Col>
                  <Col sm={6} className="p-2 text-start divider">
                    <span class="badge rounded-pill fw-bold border-0 text-dark p-3 fs-6 themeColorUser inputPostFile">
                      <FcVideoCall className="icon" /> Videos
                      <input type="file" />
                    </span>
                  </Col>
                </Row>

                <div class="modal-footer">
                  <button type="submit" class="btn btn-primary">
                    Post
                  </button>
                </div>
              </div>
              </Form>
            </div>
          </div>
        </div>
      </Container>
    </div>
  );
}

export default CreatePost;
