import React, { useState, useEffect, Fragment } from "react";
import { useNavigate } from "react-router-dom";
import Axios from "axios";
import { BiPencil, BiMinusCircle } from "react-icons/bi";
import LocationIcon from "../../../Assests/Images/icon/location.svg";

import { Col, Row, Modal, Button, Form } from "react-bootstrap";
import Heading from "../../Heading";
import { toast } from "react-toastify";

import useInput from "../../../Helpers/use-input";

const isNotEmpty = (value) => value.trim() !== "";


function About() {
  const navigate = useNavigate();

  const [about, setAbout] = useState([]);
  const [show, setShow] = useState(false);

  useEffect(() => {
    if (!localStorage.getItem("accessToken")) {
      navigate("/signup");
    } else {
      Axios.get(
        `${process.env.REACT_APP_SERVER_DOMAIN}/dashboardUserProfile/about/`,
        {
          headers: {
            accessToken: localStorage.getItem("accessToken"),
          },
        }
      ).then((response) => {
        if (response.data.error) {
          return toast.error(
            <Fragment>
              <BiMinusCircle /> <span>{response.data.error}</span>
            </Fragment>
          );
        } else {
          setAbout(response.data);
        }
      });
    }
  }, []);


    //DOB Input Check
  //   const {
  //     value: enteredName,
  //     isValid: enteredNameIsValid,
  //     hasError: nameInputHasError,
  //     valueChangeHandler: nameChangeHandler,
  //     inputBlurHandler: nameBlurHandler,
  //     reset: resetNameInput,
  //   } = useInput(isNotEmpty);

  // let aboutMeFormIsValid = false;

  // if(enteredNameIsValid) {
  //   aboutMeFormIsValid = true;
  // }

  const aboutMeFormSubmissionHandler = (event) => {
    console.log(event.target.value)
    event.preventDefault();

    // if(!enteredNameIsValid) {
    //   return;
    // } else {
    //   alert("sending")
    // }

    // resetNameInput();
  };

  const nameClasses = "form-control form-control-sm";

  // const nameClasses = nameInputHasError
  // ? "form-control form-control-sm is-invalid"
  // : "form-control form-control-sm";

  return (
    <>
      <Row className="mx-auto border rounded p-2 my-2">
        <Col sm={12}>
          <Row>
            <Col xs={10} sm={10} className="text-start">
              <Heading content="About Me" design="h5" />
            </Col>
            <Col xs={2} sm={2} className="text-end align-self-center">
              <p
                role="button"
                className="my-auto"
                onClick={() => setShow(true)}
              >
                <BiPencil className="icon" />
              </p>
            </Col>
          </Row>
        </Col>
        <Col sm={12}>
          <Col>
            {about.map((aboutDetails) => {
              const enteredAbout = aboutDetails.about_user;
              const ok = aboutDetails.name;
              const enteredPhone = aboutDetails.contnum;
              const enteredEmail = aboutDetails.email;
              const enteredGender = aboutDetails.gender;
              const dob = new Date(aboutDetails.dob);
              const enteredDob = dob.toISOString().substr(0, 10);
              const enteredNationality = aboutDetails.nationality;
              const enteredAddress = aboutDetails.user_address;

              return (
                <>
                  { enteredAddress === 'null' &&
                  <p className="m-0 text-start fs-7 text-capitalize">
                    <img src={LocationIcon} className="icon" />
                    {enteredAddress}
                  </p>
                  }
                  <p className="fs-7 text-truncate-3 text-justify">
                    {enteredAbout}
                  </p>

                  <Modal
                    size="md"
                    scrollable
                    show={show}
                    onHide={() => setShow(false)}
                  >
                    <Modal.Header closeButton>
                      <Modal.Title>
                        <Heading content="About Me" design="h4 my-auto" />
                      </Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                      <Form onSubmit={aboutMeFormSubmissionHandler}>
                        <Col sm={12} className="form-group mb-3">
                          <label htmlFor="fullName" className="required">
                            Full Name
                          </label>
                          <input
                            type="text"
                            placeholder="Enter your full name"
                            className={nameClasses}
                            // onChange={nameChangeHandler}
                            // onBlur={nameBlurHandler}
                            name="fname"
                            defaultValue={ok}
                            // value={enteredName}
                          />

                          {/* {nameInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Please fill in your full name.
                      </Form.Control.Feedback>
                    )} */}
                        </Col>

                        <Col sm={12} className="form-group mb-3">
                          <label htmlFor="fullName" className="required">
                            Contact Number
                          </label>
                          <input
                            type="text"
                            placeholder="Enter your Contact Number"
                            className="form-control form-control-sm"
                            // className={nameClasses}
                            // onChange={nameChangeHandler}
                            // onBlur={nameBlurHandler}
                            value={enteredPhone}
                          />

                          {/* {nameInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Please fill in person-in-charge name.
                      </Form.Control.Feedback>
                    )} */}
                        </Col>

                        <Col sm={12} className="form-group mb-3">
                          <label htmlFor="fullName" className="required">
                            Email Address
                          </label>
                          <input
                            plaintext
                            readOnly
                            defaultValue={enteredEmail}
                            className="form-control-plaintext form-control-sm"
                          />

                          {/* {nameInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Please fill in person-in-charge name.
                      </Form.Control.Feedback>
                    )} */}
                        </Col>

                        <Col sm={12} className="form-group mb-2">
                          <label htmlFor="gender" className="required">
                            Gender
                          </label>
                          <select
                            defaultValue={enteredGender}
                            aria-label="Gender"
                            className="form-select form-select-sm"
                            // className={locationClasses}
                            // onChange={locationChangeHandler}
                            // onBlur={locationBlurHandler}
                          >
                            <option value="Male">Male</option>
                            <option value="Female">Female</option>
                          </select>
                          {/* {locationInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Please choose your company location.
                      </Form.Control.Feedback>
                    )} */}
                        </Col>

                        <Col sm={12} className="form-group mb-2">
                          <label htmlFor="inputdob" className="required">
                            Date of Birth
                          </label>
                          <input
                            type="date"
                            id="inputdob"
                            placeholder="Your Birthday"
                            className="form-control form-control-sm"
                            // className={dobClasses}
                            // onChange={dobChangeHandler}
                            // onBlur={dobBlurHandler}
                            onChange={(event) =>
                              console.log(event.target.value)
                            }
                            defaultValue={enteredDob}
                          />
                          {/* {dobInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Please select your birthdate.
                      </Form.Control.Feedback>
                    )} */}
                        </Col>

                        <Col sm={12} className="form-group mb-2">
                          <label htmlFor="gender">Nationality</label>
                          <select
                            defaultValue={enteredNationality}
                            aria-label="Gender"
                            className="form-select form-select-sm"
                            // className={locationClasses}
                            // onChange={locationChangeHandler}
                            // onBlur={locationBlurHandler}
                          >
                            <option value="">Please Choose One</option>
                            <option value="Malaysian">Malaysian</option>
                            <option value="Others">Others</option>
