import React, { useState, useEffect, Fragment } from "react";
import Axios from "axios";
import useInput from "../../Helpers/use-input";

import Report from "./Report";
import Comment from "./Comment";

import { Container, Row, Col, Collapse, Form } from "react-bootstrap";
import {
  BsClockFill,
  BsThreeDotsVertical,
  BsTrash,
  BsPencilSquare,
} from "react-icons/bs";
import { FaThumbsUp, FaComments, FaShare } from "react-icons/fa";
import { BiMessageCheck, BiMinusCircle } from "react-icons/bi";

//Toast
import { toast } from "react-toastify";


const isNotEmpty = (value) => value.trim() !== "";

function Posts(props) {
  const userID = props.userID;
  const postOwnerID = props.postOwnerID;
  const sharePostLink = props.sharePostLink;
  const postID = props.postID;

  if (props.postCompany === 1) {
    var postCompanyLink =
      "/partner/" +
      props.postCompanyName.toLowerCase().trim().split(/\s+/).join("-");
  }

  const [viewCommentProfile, setViewCommentProfile] = useState("avatar/male.svg");
  const [viewProfile, setViewProfile] = useState("avatar/male.svg");
  const [viewComment, setViewComment] = useState([]);
  const [viewLike, setViewLike] = useState([]);

  useEffect(() => {
    Axios.get(
      `${process.env.REACT_APP_SERVER_DOMAIN}/home/userProfile/` + postOwnerID
    ).then((response) => {
      const profileDetail1 = response.data[0];
      const profileDetail2 = response.data[1];

      var profile;

      if (profileDetail1.length === 0) {
        if (profileDetail2[0].gender === "Male") {
          profile = `${process.env.REACT_APP_SERVER_DOMAIN}/public/Assests/Images/avatar/male.svg`;
          setViewCommentProfile(profile);
        } else {
          profile = `${process.env.REACT_APP_SERVER_DOMAIN}/public/Assests/Images/avatar/female.svg`;
          setViewCommentProfile(profile);
        }
      } else {
        profile =
          `${process.env.REACT_APP_SERVER_DOMAIN}/public/Assests/Images/user/` +
          profileDetail1[0].profile_photo;
        setViewCommentProfile(profile);
      }
    });
  }, [postOwnerID]);

  useEffect(() => {
    Axios.get(
      `${process.env.REACT_APP_SERVER_DOMAIN}/home/userProfile/` + userID
    ).then((response) => {
      const profileDetail1 = response.data[0];
      const profileDetail2 = response.data[1];

      var profile;

      if (profileDetail1.length === 0) {
        if (profileDetail2[0].gender === "Male") {
          profile = `${process.env.REACT_APP_SERVER_DOMAIN}/public/Assests/Images/avatar/male.svg`;
          setViewProfile(profile);
        } else {
          profile = `${process.env.REACT_APP_SERVER_DOMAIN}/public/Assests/Images/avatar/female.svg`;
          setViewProfile(profile);
        }
      } else {
        profile =
          `${process.env.REACT_APP_SERVER_DOMAIN}/public/Assests/Images/user/` +
          profileDetail1[0].profile_photo;
        setViewProfile(profile);
      }
    });
  }, [userID]);


  useEffect(() => {
    Axios.get(
      `${process.env.REACT_APP_SERVER_DOMAIN}/home/commentlist/` + postID
    ).then((response) => {
      setViewComment(response.data);
    });
  }, [postID]);

  useEffect (() => {
    Axios.get(
      `${process.env.REACT_APP_SERVER_DOMAIN}/home/like`,
      {
        postID:postID,
        userID:userID,
      }
    ).then((response) => {
      setViewLike(response.data);
    });
  },[postID, userID]);


      //COMMENT Input Check
      const {
        value: enteredComment,
        isValid: enteredCommentIsValid,
        hasError: commentInputHasError,
        valueChangeHandler: commentChangeHandler,
        inputBlurHandler: commentBlurHandler,
        reset: resetCommentInput,
      } = useInput(isNotEmpty);

    let commentFormIsValid = false;

    if(enteredComment !== "") {
      commentFormIsValid = true;
    }

    const commentFormSubmissionHandler = (event) => {
      event.preventDefault();

      if(!enteredCommentIsValid) {
        return;
      } else {
        Axios.post(
          `${process.env.REACT_APP_SERVER_DOMAIN}/home/comment/`,
          {
            userID:userID,
            postID:postID,
            enteredComment:enteredComment,
          }
        ).then ((response) => {
          // const commentToAdd = {
          //   comment:enteredComment,
          // };

          // const comment = "ss";
          setViewComment([...viewComment]);
          // alert("jiji")
          // window.location.reload();
        })
      }
      resetCommentInput();
    };

    const commentClasses = commentInputHasError
      ? "form-control form-control-sm is-invalid"
      : "form-control form-control-sm";


  function copyPostLink(sharePostLink) {
    navigator.clipboard.writeText(sharePostLink);
    return toast.dark(
      <Fragment>
        <BiMessageCheck />
        <span>Copied to the clipboard!</span>
      </Fragment>
    );
  }

  const [open, setOpen] = useState(false);


  const deletePost =(event) => {
    const deletePostID = event.target.value;

    Axios.patch(
      `${process.env.REACT_APP_SERVER_DOMAIN}/home/deletePost/` + deletePostID
    ).then ((response) => {
      if (response.data.error) {
        return toast.error(
          <Fragment>
            <BiMinusCircle /> <span>{response.data.error}</span>
          </Fragment>
        );
      } else {
        return [toast.success(
          <Fragment>
            <BiMessageCheck /> <span>{response.data.success}</span>
          </Fragment>
        ), window.location.reload()]
      }

    })
  }

  return (
    <Container id={postID} className="border p-2 mb-3">
      <Row className="mb-3">
        <Col xs={10} sm={10} className="text-start">
          <Row>
            <Col xs={2} sm={1} className="p-0 ps-2">
              <img
                src={viewCommentProfile}
                alt="Zom-IN User"
                className=" col w-100 rounded-circle"
              />
            </Col>
            <Col xs={10} sm={10} className="">
              <Row>
                <Col sm={12}>
                  <p className="my-auto fw-bold">
                    {props.postCompany === 1 ? (
                      <a
                        href={postCompanyLink}
                        target="_blank"
                        rel="noreferrer"
                        className="text-decoration-none text-dark"
                      >
                        {props.postCompanyName}
                      </a>
                    ) : (
                      <a
                        href="/home"
                        className="text-decoration-none text-dark"
                      >
                        {" "}
                        {props.postUserName}
                      </a>
                    )}
                  </p>
                </Col>
                <Col sm={12}>
                  <p className="text-muted fw-light fs-7">
                    <BsClockFill size={12} className="me-1" />
                    {/* 10th June 2021 5:14 pm */}
                    {props.postTime}
                  </p>
                </Col>
              </Row>
            </Col>
          </Row>
        </Col>
        <Col xs={2} sm={2} className="text-end">
          <div className="dropdown">
            <button
              className="btn shadow-none"
              type="button"
              id="dropdownMenuButton1"
              data-bs-toggle="dropdown"
              aria-expanded="false"
            >
              <BsThreeDotsVertical />
            </button>
            <ul
              className="dropdown-menu dropdown-menu-end"
              aria-labelledby="dropdownMenuButton1"
            >
            {props.postOwnerID === userID && <> 
              <li>
                <button
                  type="button"
                  className="btn btn-link dropdown-item"
                >
                  <BsPencilSquare className="icon" />
                  Edit
                </button>
              </li>
              <li>
                <button
                  type="button"
                  className="btn btn-link dropdown-item"
                  value={postID}
                  onClick={deletePost}
                >
                  <BsTrash className="icon" /> Delete
                </button>
              </li>
              <li>
                <hr className="dropdown-divider" />
              </li>
              </>}
              <li>
                <Report 
                  postID={postID} 
                  userID={userID}
                  postOwnerID={postOwnerID}
                  />
              </li>
            </ul>
          </div>
        </Col>
      </Row>
      <Row className="mb-3">
        <Col sm={12} className="px-3">

          {props.postText !== "" && (
            <div
            dangerouslySetInnerHTML={{
              __html: props.postText.replace(/href/g, "target='_blank' href"),
            }}
          />
          )}
          {props.postImageName !== "" && (
            <img src={props.postImage} alt="rvr" className="w-100 mb-2" />
          )}
          {props.postVideoName !== "" && (
            <video controls className="d-flex w-75 mx-auto">
              <source
                className="w-75"
                alt="Video Uplnoad"
                src={props.postVideo}
              />
            </video>
          )}
        </Col>
      </Row>
      <Row>
        <Col sm={12}>
          <hr className="dropdown-divider" />
        </Col>
        {viewLike.map((likeDetails, key) => {
          const likeUserID = likeDetails.user_id;

          return (
            <>
            {likeUserID === userID ?  
            <Col xs={4} sm={4} className="text-center link-secondary">
        <FaThumbsUp /> Like
        </Col> 
        : 
        <Col xs={4} sm={4} className="text-center link-secondary">
        <FaThumbsUp /> Likes
        </Col>}
        </>
          )
      })}


        <Col
          xs={4}
          sm={4}
          className="text-center link-secondary border-start border-end px-0 px-md-1 px-lg-1"
          onClick={() => setOpen(!open)}
          aria-expanded={open}
          aria-controls="collapseID"
          role="button"
        >
            <FaComments className="icon icon-xs" />
            Comment
        </Col>
        <Col
          xs={4}
          sm={4}
          className="text-center link-secondary "
          role="button"
          onClick={() => copyPostLink(sharePostLink)}
        >
          <FaShare className="icon icon-xs" />
          Share
        </Col>
      </Row>
      <Collapse in={open} className="pt-1 px-1" id="collapseID">
        <Container>
        <Form onSubmit={commentFormSubmissionHandler}>
          <Row className="align-items-center py-3">

            <Col xs={1} sm={1} className="p-0 ps-2 ps-xs-1">
              <img
                src={viewProfile}
                alt="eger"
                className=" col w-100 rounded-circle"
              />
            </Col>
            <Col xs={8} sm={9} className="pe-xs-0">
              <div className="">
                <input
                  type="text"
                  id="comment"
                  placeholder="Comments"
                  autoComplete="off"
                  className={commentClasses}
                    onChange={commentChangeHandler}
                    onBlur={commentBlurHandler}
                    value={enteredComment}
                />

                {commentInputHasError && (
                  <Form.Control.Feedback type="invalid">
                      Please share your thought on this post.
                    </Form.Control.Feedback>
                  )}
              </div>
            </Col>
            <Col xs={2} sm={2} className="p-0">
              <button 
              className="btn btn-sm btn-primary"
              type="submit"
              disabled={!commentFormIsValid}>
                Comment
              </button>
            </Col>

          </Row>
          </Form>

          {viewComment.map((commentDetails, key) => {
              const commentID = commentDetails.commentID
              const postID = commentDetails.post_id;
              const comment = commentDetails.comment;
              const commentOwnerID = commentDetails.comment_user_id;
              const companyRole = commentDetails.company;
              const commentUserName = commentDetails.name;
              const commentCompanyName = commentDetails.companyname;
              const comment_time = new Date(commentDetails.comment_time);

              const commentTime = comment_time
                .toLocaleDateString("en-GB", {
                  day: "numeric",
                  month: "short",
                  year: "numeric",
                  hour: "2-digit",
                  minute: "2-digit",
                  hour12: true,
                })
                .replace(/ /g, " ");
              return (
                <Comment 
                  key={commentID}
                  postID={postID}
                  comment={comment}
                  commentTime={commentTime}
                  commentOwnerID={commentOwnerID}
                  companyRole={companyRole}
                  commentUserName={commentUserName}
                  commentCompanyName={commentCompanyName}
                />
              );

          })}
        </Container>
      </Collapse>
    </Container>
  );
}

export default Posts;
